mapper.select:
SELECT GS.PRODUCT_NO, GS.PRODUCT_CATEGORY, GS.PRODUCT_NAME, GS.PRODUCT_CONTENT, GS.PRODUCT_ORIGIN_PRICE, GS.PRODUCT_SELL_PRICE, GS.PRODUCT_SALE_PRICE, GS.PRODUCT_DATE, GS.PRODUCT_KEYWORD, GS.PRODUCT_READCNT, GS.PRODUCT_PICK, GS.PRODUCT_THUMBNAIL, GS.GUBUN, GI.PRODUCT_IMAGE_STD FROM PRODUCT GS, PRODUCT_IMAGE GI WHERE GS.PRODUCT_NO = GI.PRODUCT_NO AND PRODUCT_CATEGORY = #{cate}
AND (PRODUCT_NAME LIKE '%' || #{keyword} || '%' OR PRODUCT_KEYWORD LIKE '%' || #{keyword} || '%')
AND GUBUN = '0'
ORDER BY ${orderBy} ${orderSort}

mapper.update:

UPDATE  PRODUCT
SET     GUBUN = #{GUBUN}
WHERE   PRODUCT_NO = #{IDX}


UPDATE  PRODUCT 
SET     PRODUCT_THUMBNAIL = #{PRODUCT_THUMBNAIL}
WHERE   PRODUCT_NO = #{IDX}

mapper.delete:

DELETE 
FROM    PRODUCT_LIKE 
WHERE   MEMBER_NO = #{MEMBER_NO}
AND     PRODUCT_NO = #{PRODUCT_NO}

mapper.update:

UPDATE  PRODUCT_ATTRIBUTE
SET     PRODUCT_ATT_AMOUNT = PRODUCT_ATT_AMOUNT-#{ORDER_DETAIL_AMOUNT}
WHERE   PRODUCT_ATT_NO = #{PRODUCT_ATT_NO}
